.section-metadata {
    display: none;
}

/* Metadata Badge (optional sichtbar für Maintainer) */
.section-metadata-badge {
    position: absolute;
    top: 10px;
    right: 10px;
    background-color: var(--color-info);
    color: var(--color-surface-elevated);
    padding: 4px 8px;
    border-radius: 4px;
    font-size: 0.75em;
    font-weight: 600;
    cursor: pointer;
    transition: all var(--transition-fast);
    display: none; /* Standardmäßig versteckt */
}

body.maintainer-mode .section-metadata-badge {
    display: block;
}

.section-metadata-badge:hover {
    background-color: var(--color-primary);
    transform: scale(1.05);
}

/* Veraltete Content-Warnung */
.content-section[data-meta-status="outdated"] {
    position: relative;
}

.content-section[data-meta-status="outdated"]::before {
    content: '⚠️ Inhalt möglicherweise veraltet';
    position: absolute;
    top: -10px;
    right: 10px;
    background-color: var(--color-warning);
    color: var(--color-warning-text);
    padding: 6px 12px;
    border-radius: 6px;
    font-size: 0.85em;
    font-weight: 600;
    display: none;
}

body.maintainer-mode .content-section[data-meta-status="outdated"]::before {
    display: block;
}

/* Metadata Panel (Overlay) */
.metadata-panel {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) scale(0.9);
    max-width: 700px;
    width: 90%;
    max-height: 80vh;
    background-color: var(--color-surface-elevated);
    border: 2px solid var(--color-border-strong);
    border-radius: 12px;
    box-shadow: 0 12px 48px var(--color-shadow-strong);
    z-index: 10000;
    opacity: 0;
    pointer-events: none;
    transition: all 0.3s ease;
    overflow: hidden;
    display: flex;
    flex-direction: column;
}

.metadata-panel.visible {
    opacity: 1;
    pointer-events: auto;
    transform: translate(-50%, -50%) scale(1);
}

.metadata-panel-header {
    padding: 20px;
    background: linear-gradient(135deg, var(--color-primary), var(--color-primary-hover));
    color: var(--color-surface-elevated);
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.metadata-panel-title {
    margin: 0;
    font-size: 1.2em;
    font-weight: 600;
}

.metadata-panel-close {
    background: rgba(255, 255, 255, 0.2);
    border: none;
    color: white;
    font-size: 1.5em;
    width: 32px;
    height: 32px;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: all var(--transition-fast);
}

.metadata-panel-close:hover {
    background: rgba(255, 255, 255, 0.3);
    transform: scale(1.1);
}

.metadata-panel-body {
    flex: 1;
    padding: 20px;
    overflow-y: auto;
}

.metadata-group {
    margin-bottom: 24px;
}

.metadata-group-title {
    font-size: 1.1em;
    font-weight: 600;
    color: var(--color-primary);
    margin-bottom: 12px;
    padding-bottom: 8px;
    border-bottom: 2px solid var(--color-border);
}

.metadata-item {
    display: flex;
    padding: 8px 0;
    gap: 12px;
}

.metadata-label {
    font-weight: 500;
    color: var(--color-text-secondary);
    min-width: 140px;
}

.metadata-value {
    color: var(--color-text-primary);
    flex: 1;
}

.metadata-value code {
    background-color: var(--color-surface);
    padding: 2px 6px;
    border-radius: 3px;
    font-family: 'Courier New', monospace;
    font-size: 0.9em;
}

.metadata-value a {
    color: var(--color-primary);
    text-decoration: none;
}

.metadata-value a:hover {
    text-decoration: underline;
}

/* Changelog */
.metadata-changelog {
    list-style: none;
    padding: 0;
}

.metadata-changelog-item {
    padding: 12px;
    background-color: var(--color-surface);
    border-left: 3px solid var(--color-info);
    margin-bottom: 8px;
    border-radius: 4px;
}

.metadata-changelog-date {
    font-weight: 600;
    color: var(--color-primary);
    margin-bottom: 4px;
}

.metadata-changelog-version {
    display: inline-block;
    background-color: var(--color-primary);
    color: var(--color-surface-elevated);
    padding: 2px 8px;
    border-radius: 4px;
    font-size: 0.8em;
    margin-left: 8px;
}

.metadata-changelog-desc {
    margin: 4px 0 0 0;
    color: var(--color-text-secondary);
}

/* Status Badges */
.metadata-status-badge {
    display: inline-block;
    padding: 4px 12px;
    border-radius: 12px;
    font-size: 0.85em;
    font-weight: 600;
}

.metadata-status-badge.approved {
    background-color: var(--color-success);
    color: var(--color-surface-elevated);
}

.metadata-status-badge.review {
    background-color: var(--color-warning);
    color: var(--color-warning-text);
}

.metadata-status-badge.outdated {
    background-color: var(--color-error);
    color: var(--color-surface-elevated);
}

.metadata-status-badge.draft {
    background-color: var(--color-border-strong);
    color: var(--color-text-primary);
}

/* Maintainer Mode Toggle */
.maintainer-mode-toggle {
    position: fixed;
    bottom: 20px;
    right: 20px;
    background-color: var(--color-primary);
    color: var(--color-surface-elevated);
    border: none;
    padding: 10px 16px;
    border-radius: 8px;
    cursor: pointer;
    font-weight: 600;
    box-shadow: 0 4px 12px var(--color-shadow);
    transition: all var(--transition-fast);
    z-index: 9999;
}

.maintainer-mode-toggle:hover {
    background-color: var(--color-primary-hover);
    transform: translateY(-2px);
    box-shadow: 0 6px 16px var(--color-shadow-strong);
}

body.maintainer-mode .maintainer-mode-toggle {
    background-color: var(--color-success);
}
